import React from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls } from 'three-stdlib'
import RotatingCube from './components/simple_cube/cube';
import './App.css';

function App() {
  return (
    <div className="App" style={{ width: '100vw', height: '100vh' }}>
      {/* Info overlay */}

      <div className = "start-page">
        <div className="save-the-date-title">
          <h1 className="save-the-date-text">SAVE THE DATE</h1>
        </div>
        <div className="subtitle">
          <h1 className="subtitle-text">Ulepšajte događaj vašim prisustvom</h1>
        </div>
        <div className="names-title">

        </div>

      </div>

      {/* Three.js Canvas */}
      <Canvas 
        camera={{ position: [0, 0, 5], fov: 75 }}
        style={{ 
          position: 'fixed',
          top: 0,
          left: 0,
          width: '100vw',
          height: '100vh',
          pointerEvents: 'none',  // Makes canvas non-interactive, allows clicks to pass through
          zIndex: 1000,          // High z-index to stay on top
          background: 'transparent'
        }}
        gl={{ alpha: true }}     // Enable alpha/transparency
      >
        {/* Lighting */}
        <ambientLight intensity={0.6} />
        <directionalLight position={[10, 10, 5]} intensity={0.8} />

        {/* 3D Objects */}
        <RotatingCube color="hotpink" speed={0.01} />

      </Canvas>
    </div>
  );
}

export default App;
